// Test the Response message fix
const testData = {
  "conversation_id": "test-123",
  "history": [
    [
      { content: { Prompt: { prompt: "Hello" } } },
      { Response: { message_id: "msg-123", content: "Now let's start the ideation process! This should be visible." } }
    ]
  ]
};

// Test isQDeveloperFormat detection
function isQDeveloperFormat(data) {
  return data.conversation_id && 
         data.history && 
         Array.isArray(data.history)
}

console.log('=== TESTING THE REAL FIX ===');
console.log('1. isQDeveloperFormat detection:', isQDeveloperFormat(testData));

// Simulate the fixed parseQDeveloperFormat logic for Response messages
const messages = [];
let messageId = 1;

for (const historyItem of testData.history) {
  if (Array.isArray(historyItem) && historyItem.length >= 2) {
    const [userTurn, assistantTurn] = historyItem;

    // Extract user message
    if (userTurn?.content?.Prompt?.prompt) {
      messages.push({
        id: (messageId++).toString(),
        type: 'human',
        content: userTurn.content.Prompt.prompt,
        timestamp: new Date().toISOString()
      });
    }

    // Handle Response messages (THE FIX!)
    if (assistantTurn?.Response) {
      messages.push({
        id: (messageId++).toString(),
        type: 'agent',
        content: assistantTurn.Response.content,
        timestamp: new Date().toISOString(),
        metadata: {
          messageId: assistantTurn.Response.message_id
        }
      });
    }
  }
}

console.log('\n2. Parsed messages count:', messages.length);
console.log('\n3. Messages:');
messages.forEach((msg, i) => {
  console.log(`   Message ${i + 1}:`);
  console.log(`   - Type: ${msg.type}`);
  console.log(`   - Content: ${msg.content.substring(0, 50)}...`);
  if (msg.metadata) console.log(`   - Metadata:`, msg.metadata);
});

console.log('\nâœ… Response message fix should work!');

